import React, { useState, useEffect } from "react";
import ScreenHeading from "../../utilities/ScreenHeading/ScreenHeading";
import ScrollService from "../../utilities/ScrollService";
import Animations from "../../utilities/Animations";
import "./Resume.css";

const Resume = (props) => {
  /* STATES */
  const [selectedBulletIndex, setSelectedBulletIndex] = useState(0);
  const [carousalOffsetStyle, setCarousalOffsetStyle] = useState({});

  let fadeInScreenHandler = (screen) => {
    if (screen.fadeInScreen !== props.id) return;

    Animations.animations.fadeInScreen(props.id);
  };
  const fadeInSubscription =
    ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler);

  /* REUSABLE MINOR COMPONENTS */
  const ResumeHeading = (props) => {
    return (
      <div className="resume-heading">
        <div className="resume-main-heading">
          <div className="heading-bullet"></div>
          <span>{props.heading ? props.heading : ""}</span>
          {props.fromDate && props.toDate ? (
            <div className="heading-date">
              {props.fromDate + "-" + props.toDate}
            </div>
          ) : (
            <div></div>
          )}
        </div>
        <div className="resume-sub-heading">
          <span>{props.subHeading ? props.subHeading : ""}</span>
        </div>
        <div className="resume-heading-description">
          <span>{props.description ? props.description : ""}</span>
        </div>
      </div>
    );
  };

  /* STATIC RESUME DATA FOR THE LABELS*/
  const resumeBullets = [
    { label: "Education", logoSrc: "education.svg" },
    { label: "Work History", logoSrc: "work-history.svg" },
    { label: "Programming Skills", logoSrc: "programming-skills.svg" },
    { label: "Projects", logoSrc: "projects.svg" },
    { label: "Interests", logoSrc: "interests.svg" },
  ];

  //here we have
  const programmingSkillsDetails = [
    { skill: "JavaScript", ratingPercentage: 85 },
    { skill: "React JS", ratingPercentage: 90 },
    { skill: "Node JS", ratingPercentage: 90 },
    { skill: "Express JS", ratingPercentage: 89 },
    { skill: "Mongo Db", ratingPercentage: 65 },
    { skill: "HTML", ratingPercentage: 80 },
    { skill: "CSS", ratingPercentage: 80 },
    { skill: "Tailwind CSS", ratingPercentage: 85 },
    { skill: "Core Java", ratingPercentage: 50 },
    { skill: "MySql", ratingPercentage: 60 },
  ];

  const projectsDetails = [
    {
      title: "Personal Portfolio Website",
      duration: { fromDate: "2023", toDate: "2024" },
      description:
        "A Personal Portfolio website to showcase all my details and projects at one place.",
      subHeading: "Technologies Used: React JS, Bootsrap",
      url: 'https://easyshop-9a676ebc47a9.herokuapp.com'

    },
    {
      title: "EasyShop ",
      duration: { fromDate: "2023", toDate: "2024" },
      description:
        "An ecommerce application designed to sell products online wth payment system integration",
      subHeading:
        "Technologies Used:  React.js, Mongo DB, Express Js, Node Js, Redux, Bootswatch",
      url: 'https://easyshop-9a676ebc47a9.herokuapp.com'
    },
    {
      title: "Youtube Clone Website ",
      duration: { fromDate: "2023", toDate: "2023" },
      description:
        " Developed a YouTube clone using React.js and Redux, integrating the YouTube Data API for seamless video browsing and enabling infinite scrolling for a continuous video feed experience, mirroring core features of the platform. ",
      subHeading:
        "Technologies Used: React Js, Redux, Tailwind CSS.",
      url: 'https://660285d4ed368cba58b09bb1--starlit-twilight-327931.netlify.app/'
    },
  ];

  const resumeDetails = [
    <div className="resume-screen-container" key="education">
      <ResumeHeading
        heading={"Biju Patnaik University of Technology (BPUT), Rourkela"}
        subHeading={"Master of Computer Applications"}
        fromDate={"2021"}
        toDate={"2023"}
      />

      <ResumeHeading
        heading={"Utkal University,Bhubaneswar"}
        subHeading={"Bachelor of Science degree in Physics"}
        fromDate={"2017"}
        toDate={"2020"}
      />
      <ResumeHeading
        heading={"Council of Higher Secondary Education, Odisha "}
        subHeading={"Science"}
        fromDate={"2015"}
        toDate={"2017"}
      />
      <ResumeHeading
        heading={"Board of Secondary Education, Odisha "}
        subHeading={"Matriculation"}
        fromDate={"2010"}
        toDate={"2015"}
      />
    </div>,

    /* WORK EXPERIENCE */
    <div className="resume-screen-container" key="work-experience">
      <div className="experience-container">
        <ResumeHeading
          heading={"LiveDigital Technoloy"}
          subHeading={"FRONTEND  DEVELOPER INTERN"}
          fromDate={"Jul 2023"}
          toDate={"Oct 2023"}
        />
        <div className="experience-description">
          <span className="resume-description-text">
            - Developed and implemented user interface components using React.js, ensuring adherence to well-known workflows like Redux.
          </span>
        </div>
        <div className="experience-description">
          <span className="resume-description-text">
            - Proficient in developing dynamic web interfaces using React.js, with experience in component-based architecture and React Router for navigation

          </span>
          <br />
          <span className="resume-description-text">
            - Collaborated with cross-functional teams on different layers of the infrastructure to achieve cohesive and high-quality applications.{" "}
          </span>
          <br />
          <span className="resume-description-text">
            - Owned and resolved complex JavaScript, ES6, and React coding challenges, contributing to successful product launches.
          </span>
          <br />
        </div>
      </div>
    </div>,

    /* PROGRAMMING SKILLS */
    <div
      className="resume-screen-container programming-skills-container"
      key="programming-skills"
    >
      {programmingSkillsDetails.map((skill, index) => (
        <div className="skill-parent" key={index}>
          <div className="heading-bullet"></div>
          <span>{skill.skill}</span>
          <div className="skill-percentage">
            <div
              style={{ width: skill.ratingPercentage + "%" }}
              className="active-percentage-bar"
            ></div>
          </div>
        </div>
      ))}
    </div>,

    /* PROJECTS */
    <div className="resume-screen-container" key="projects">
      {projectsDetails.map((project, index) => (
        <div key={index}>
          <ResumeHeading
            heading={
              <a href={project.url} target="_blank" rel="noopener noreferrer">
                {project.title}
              </a>
            }
            subHeading={project.subHeading}
            description={project.description}
            fromDate={project.duration.fromDate}
            toDate={project.duration.toDate}
          />
        </div>
      ))}
    </div>,

    /* Interests */
    <div className="resume-screen-container" key="interests">
      <ResumeHeading
        heading="Gaming"
        description="Passionate about online gaming, adept at teamwork, strategy, and problem-solving in virtual environments."
      />
      <ResumeHeading
        heading="Travel Enthusiast"
        description="Passionate about exploring new cultures and destinations, fostering adaptability, curiosity, and a global perspective."
      />
      <ResumeHeading
        heading=" Outdoor Activities"
        description="Engaging in outdoor activities such as hiking, cycling, or camping can showcase your well-roundedness and ability to maintain a healthy work-life balance. "
      />
    </div>,
  ];

  const handleCarousal = (index) => {
    let offsetHeight = 360;

    let newCarousalOffset = {
      style: { transform: "translateY(" + index * offsetHeight * -1 + "px)" },
    };

    setCarousalOffsetStyle(newCarousalOffset);
    setSelectedBulletIndex(index);
  };

  const getBullets = () => {
    return resumeBullets.map((bullet, index) => {
      const BulletImage = require(`../../../assests/${bullet.logoSrc}`).default;

      return (
        <div
          onClick={() => handleCarousal(index)}
          className={
            index === selectedBulletIndex ? "bullet selected-bullet" : "bullet"
          }
          key={index}
        >
          <img
            className="bullet-logo"
            src={BulletImage}
            alt=""
          />
          <span className="bullet-label">{bullet.label}</span>
        </div>
      );
    });
  };


  const getResumeScreens = () => {
    return (
      <div
        style={carousalOffsetStyle.style}
        className="resume-details-carousal"
      >
        {resumeDetails.map((ResumeDetail) => ResumeDetail)}
      </div>
    );
  };

  useEffect(() => {
    return () => {
      /* UNSUBSCRIBE THE SUBSCRIPTIONS */
      fadeInSubscription.unsubscribe();
    };
  }, [fadeInSubscription]);

  return (
    <div className="resume-container screen-container " id={props.id || ""}>
      <div className="resume-content">
        <ScreenHeading title={"Resume"} subHeading={"My formal Bio Details"} />
        <div className="resume-card">
          <div className="resume-bullets">
            <div className="bullet-container">
              <div className="bullet-icons"></div>
              <div className="bullets">{getBullets()}</div>
            </div>
          </div>

          <div className="resume-bullet-details">{getResumeScreens()}</div>
        </div>
      </div>
    </div>
  );
};

export default Resume;
